function (searchCriteria, item) {
  	if (typeof searchCriteria === "string") {
    	searchCriteria = JSON.parse(searchCriteria);
    }
  	var d = new Date(item.uploaded_dt);
	if (searchCriteria.date === "today") {
    	return d > moment().startOf('day');
    } else if (searchCriteria.date === "week") {
        return d > moment().startOf('week');
	} else {
      	return true;
    }
}

